import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from "@mui/material/Typography";
import * as React from 'react';
import { Grid } from "@mui/material";

function ProjectCard({id, name, email, description}){
      const card = (
        <React.Fragment>
            <CardContent>
                <Typography sx={{ fontSize: 20, fontWeight: "bold" }} color="text.secondary" gutterBottom>
                    {name}
                </Typography>
                <Typography sx={{fontSize: 15}} component="div">
                    Owner's email: {email}
                </Typography>
                <Typography sx={{ mb: 0.6 }} color="text.secondary">
                    Project description: {description}
                </Typography>
                <CardActions>
                <Button size="small">
                    Learn More
                </Button>
            </CardActions>
            </CardContent>
        </React.Fragment>
    );

    return(
    <>
        <Grid item xs={10} md={5} lg={5}>
            <Box sx={{ minWidth: 275 }}>
                <Card variant="outlined">{card}</Card>
            </Box>
        </Grid>
    </>
    )
}

export default ProjectCard
