import * as React from 'react';
import BottomNavigation from '@mui/material/BottomNavigation';
import BottomNavigationAction from '@mui/material/BottomNavigationAction';
import RestoreIcon from '@mui/icons-material/Restore';
import FavoriteIcon from '@mui/icons-material/Favorite';
import { useTheme } from '@mui/material/styles';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import AddBoxIcon from '@mui/icons-material/AddBox';

export default function LabelBottomNavigation() {
  const [value, setValue] = React.useState('recents');
  const [selected, setSelected] = React.useState(null); // Estado para representar o botão selecionado
  const theme = useTheme(); 

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <BottomNavigation
      sx={{
        position: 'fixed',
        bottom: 0,
        width: '100%',
        backgroundColor: theme.palette.primary.main,
      }}
      value={value}
      onChange={handleChange}
    >
      <BottomNavigationAction
        label="Recents"
        value="recents"
        icon={<RestoreIcon />}
        sx={{ color: theme.palette.secondary.main, opacity: selected === 'recents' ? 0 : 1 }}
        showLabel
      />
      <BottomNavigationAction
        label="Favorites"
        value="favorites"
        icon={<FavoriteIcon />}
        sx={{ color: theme.palette.secondary.main, opacity: selected === 'favorites' ? 0 : 1 }}
        showLabel
      />
      <BottomNavigationAction
        label="New project"
        value="New project"
        icon={<AddBoxIcon />}
        sx={{ color: theme.palette.secondary.main, opacity: selected === 'New project' ? 0 : 1 }}
        showLabel
      />
      <BottomNavigationAction
        label="User"
        value="User"
        icon={<AccountCircleIcon />}
        sx={{ color: theme.palette.secondary.main, opacity: selected === 'User' ? 0 : 1 }}
        showLabel
        href="/user"
      />
    </BottomNavigation>
  );
}
